#if( FMI_SUPPORT_A602 )
//! Structure containing details for a text message ACK from the client
struct text_msg_ack_event_type
{
    MessageId            message_id;        //!< The message ID that was ACKed
    char                 ack_text[50];      //!< Text of the ack (OK, Yes, No, or a canned response)
};

//! Structure containing details for a client to server text message
struct text_msg_from_client_event_type
{
    uint32               message_id;        //!< The message ID
    time_type            origination_time;  //!< Origination time on the client
    char                 message_text[200]; //!< Message sent by the client
#if( FMI_SUPPORT_A607 )
    MessageId            link_id;           //!< ID of server-to-client message that this is a reply for
#endif
};
#endif

#if( FMI_SUPPORT_A604 )
//! Structure containing details for a text message status from the client
struct text_msg_status_event_type
{
    MessageId                msg_id;          //!< The message ID
    fmi_A604_message_status  message_status;  //!< The status of the text message
};
#endif

#endif
